func findPacket(packetLen int, s string) int {
	for start := 0; start < len(s)-packetLen-1; start++ {
		end := start + packetLen
		window := s[start:end]

		counts := map[string]struct{}{}
		for _, char := range window {
			counts[string(char)] = struct{}{}
		}

		if len(counts) == packetLen {
			return end
		}
	}

	return 0
}

